export const pi = Math.PI;
export const tau = 2 * pi;
export const epsilon = 0.0001;
export const c1 = 1.70158;
export const c2 = c1 * 1.525;
export const c3 = c1 + 1;
export const c4 = tau / 3;
export const c5 = tau / 4.5;
